import React, { useState } from "react";

// get whats typing and put setState using useState
// put the notes to an array when add is clicked
const [note,setNote] = useState({title:"",content:""});


function CreateArea() {
  return (
    <div>
      <form>
        <input name="title" placeholder="Title" value={note.title }/>
        <textarea name="content" placeholder="Take a note..." rows="3" value={note.content} />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
