import React from "react";


// render notes from an array

function Note(props) {

  function deleteItem(){
    props.deleteItem(props.id)
  }
  return (
    <div className="note">
      <h1>{props.title}</h1>
      <p>{props.content}</p>
      <button onClick={()=>props.deleteItem(props.id)}>DELETE</button>
    </div>
  );
}

export default Note;
