import React, { useState } from "react";

// get whats typing and put setState using useState
// put the notes to an array when add is clicked



function CreateArea() {
  const [note,setNote] = useState({title:"",content:""});

  function addText(events){
    const {name,value} = events.target;
    console.log(name,value)
    // setNote(
    //   if(name==="title"){
    //     return({title:value,content:note.content});
    //   }else if(name==="content"){
    //     return({title:note.title,content:value});
    //   }
    // )
    setNote((preValues)=>{
      return {...preValues,
      [name]:value}
    })
    console.log(name,value)
  }
  function click(){

  }
  // value={note.content}
  // value={note.content}
  return (
    <div>
      <form>
        <input onChange={addText} name="title" placeholder="Title" value={note.title} />
        <textarea onChange={addText} name="content" placeholder="Take a note..." rows="3" value={note.content}  />

        <button onClick={click}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
